grid.sqlUpdate:
declare @UserId bigint = (SELECT USERID FROM DBO.AspNetUsers WHERE USERNAME = @__LoginId__)
exec [dbo].[ConfigScheduleDay_Upsert]
@ConfigScheduleDayId = @CONFIGSCHEDULEDAYID,
@WeekDay = @WEEKDAYNBR,
@RoleId = @ROLEID,
@OrganizationId = @ORGANIZATION_ID,
@ResourceCount = @RESOURCECOUNT,
@IsActive = @ISACTIVE,
@UserId = @UserId,
@LocationID = @LOCATIONLOOKUPID

grid.sqlInsert:
declare @UserId bigint = (SELECT USERID FROM DBO.AspNetUsers WHERE USERNAME = @__LoginId__)
exec [dbo].[ConfigScheduleDay_Upsert]
@ConfigScheduleDayId = null,
@WeekDay = @WEEKDAYNBR,
@RoleId = @ROLEID,
@OrganizationId = @ORGANIZATION_ID,
@ResourceCount = @RESOURCECOUNT,
@IsActive = @ISACTIVE,
@UserId = @UserId,
@LocationID = @LOCATIONLOOKUPID
